def pedestal(n):
    place_2 = n // 3
    remain = n % 3
    if remain:
        place_2 += 1
        remain = 3 - remain
    place_1 = place_2 + 1
    place_3 = place_2 - remain - 1
    if place_3 < 1:
        make_over = 1 - place_3
        place_3 += make_over
        place_2 -= make_over
    return (place_2, place_1, place_3)
output = []
for _ in range(int(input())):
    n = int(input())
    output.append(pedestal(n))
for _ in output:
    print(*_)

