def solve(a, b, n):
    for i in range(n):
        if a[i] < b[i]:
            return False
    diff = math.inf
    for i in range(n):
        if b[i] != 0:
            diff = min(diff, a[i] - b[i])
    if diff == math.inf:
        return True
    for i in range(n):
        if a[i] - b[i] > diff:
            return False
        if b[i] != 0 and a[i] - b[i] < diff:
            return False
    return True

