def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)

def shift(s):
    for i in range(1, len(s) + 1):
        if s == s[i:] + s[:i]:
            return i

def prefix(a):
    res = [0]
    for num in a:
        res.append(res[-1] + num)
