def func(n, a, b):
    error_zero = 0
    error_one = 0
    for i in range(n):
        if a[i] == 0 and b[i] == 1:
            error_zero += 1
        elif a[i] == 1 and b[i] == 0:
            error_one += 1
    rearrange = min(error_zero, error_one)
    if rearrange > 0:
        error_one -= rearrange
        error_zero -= rearrange
        step = 1
    else:
        step = 0
    return max(error_one, error_zero) + step
