def power(a, b, mod):
    if b == 1:
        return a
    if b == 0:
        return 1
    ans = power(a, b // 2, mod)
    ans *= ans
    if b % 2:
        ans *= a
    ans = ans % mod
    return ans

def factorial(n, start=1, skip=None):
    res = start
    for i in range(1, n + 1):
        if i == skip:
            continue
        res *= i
    return res

def make_deg(l):
