def q(n, a, b):
    for i in range(n):
        if b[i] < a[i]:
            (b[i], a[i]) = (a[i], b[i])
    w = 0
    for i in range(n - 1):
        w += abs(a[i] - a[i + 1]) + abs(b[i] - b[i + 1])
    return w
