def swap(a, b, l):
    for i in range(l):
        if a[i] > b[i]:
            (a[i], b[i]) = (b[i], a[i])
    return (a, b)

def minsum(a, b, l):
    av = 0
    bv = 0
    for i in range(0, l - 1):
        av += abs(a[i] - a[i + 1])
        bv += abs(b[i] - b[i + 1])
    s = abs(av + bv)
    return s
