    else:
        for i in range(1, n):
            if b[i] < a[i]:
                (a[i], b[i]) = (b[i], a[i])
    sum = 0
    for i in range(n - 1):
        if a[i + 1] > a[i]:
            sum += a[i + 1] - a[i]
