class Solution:

    def avoidLocalMax(self, n, arr):
        change_cnt = 0
        for (i, val) in enumerate(arr):
            if i == 0 or i == n - 1:
                continue
            if val > arr[i - 1] and val > arr[i + 1]:
                change_cnt += 1
                arr[i + 1] = int(max(arr[i + 2], arr[i])) if i < n - 2 else arr[i]
        return (change_cnt, arr)
