def ruleOfLeague(n, x, y):
    if x == 0 and y == 0:
        print(-1)
    elif x == 0 or y == 0:
        number = 0
        if x != 0:
            number = x
        else:
            number = y
        if n == 1:
            print(1)
        elif n == 2:
            print(2)
        elif (n - 1) % number != 0:
            print(-1)
        else:
            player = list(range(1, n + 1))
            player = player[1::number]
            winner = [1] + player[1:]
            for_print = []
            for each in winner:
                for_print += [each] * number
            print(' '.join(list(map(str, for_print))))
    else:
        print(-1)
