N = int(input())
for i in range(N):
    (k, n) = map(int, input().split())
    l = [1]
    t = 2
    for j in range(2, k + 1):
        if n - (l[-1] + t) >= k - j and n - (l[-1] + t) < n:
            l.append(l[-1] + t)
            t += 1
        else:
            l.append(l[-1] + 1)
    print(' '.join([str(x) for x in l]))

t = int(input())

def solve():
    (k, n) = list(map(int, input().split()))
    triangle = 0
    ans = []
    for i in range(1, k + 1):
        if i * (i + 1) / 2 + (k - i) <= n:
            triangle = i
        else:
            break
    for i in range(1, triangle + 1):
        ans.append(i * (i + 1) // 2)
    x = ans[-1]
    for i in range(k - triangle):
        ans.append(x + i + 1)
    for i in range(len(ans)):
        ans[i] = str(ans[i])
    print(' '.join(ans))
for i in range(t):
    solve()
