from sys import stdin, stdout

def solve():
    n = int(stdin.readline().strip())
    inputs = list(map(int, stdin.readline().strip().split(' ')))
    max_blanks = 0
    count_blank1 = 0
    for i in range(1, n):
        diff = inputs[i] - inputs[i - 1] - 1
        if diff > 2:
            print('NO')
            return
        elif diff == 2:
            if max_blanks == 2 or max_blanks == 1:
                print('NO')
                return
            else:
                max_blanks = 2
        elif diff == 1:
            count_blank1 += 1
            if max_blanks == 2:
                print('NO')
                return
            elif max_blanks == 1:
                if count_blank1 > 2:
                    print('NO')
                    return
            else:
                max_blanks = 1
    print('YES')

def main():
    t = int(stdin.readline())
    for _ in range(t):
        solve()
main()

