def eval(dots):
    lens = []
    for i in range(1, len(dots)):
        lens.append(dots[i] - dots[i - 1])
    if max(lens) > 3:
        return 'NO'
    elif max(lens) == 3 and 2 not in lens and (lens.count(3) == 1):
        return 'YES'
    elif max(lens) == 2 and lens.count(2) < 3:
        return 'YES'
    elif max(lens) == 1:
        return 'YES'
    else:
        return 'NO'

