def process(n, a, b):
    ans = 0
    i = 0
    while i < n:
        if a[i] != b[i]:
            ans += 2
            i += 1
            continue
        if a[i] == '1':
            if i + 1 < n and (a[i + 1] == '0' or b[i + 1] == '0'):
                ans += 2
                i += 2
            else:
                i += 1
        elif i + 1 < n and a[i + 1] == '1' and (b[i + 1] == '1'):
            ans += 2
            i += 2
        else:
            ans += 1
            i += 1
    return ans
