def s1(k):
    if not k:
        return 0
    ans = 0
    taken = set()
    for i in range(len(k)):
        if k[i] == '0':
            if i - 1 >= 0 and k[i - 1] == '1' and (i - 1 not in taken):
                taken.add(i - 1)
                ans += 2
            elif i + 1 < len(k) and k[i + 1] == '1' and (i + 1 not in taken):
                taken.add(i + 1)
                ans += 2
            else:
                ans += 1
    return max(ans, k.count('0'))

def solve(a, b, n):
    ans = 0
    t = ''
    for i in range(n):
        if a[i] != b[i]:
            ans += 2 + s1(t)
            t = ''
        else:
            t += a[i]
    ans += s1(t)
    return ans
