def max_mex(arr):
    ptr = 0
    tot = 0
    while ptr < len(arr[0]):
        if arr[0][ptr] != arr[1][ptr]:
            tot += 2
            ptr += 1
        elif arr[0][ptr] == '1' and ptr + 1 < len(arr[0]):
            if arr[0][ptr + 1] != arr[1][ptr + 1]:
                ptr += 1
            elif arr[0][ptr + 1] == '1':
                ptr += 1
            else:
                tot += 2
                ptr += 2
        elif arr[0][ptr] == '0' and ptr + 1 < len(arr[0]):
            if arr[0][ptr + 1] != arr[1][ptr + 1]:
                tot += 1
                ptr += 1
            elif arr[0][ptr + 1] == '1':
                ptr += 2
                tot += 2
            else:
                tot += 1
                ptr += 1
        else:
            tot += 1 if arr[0][ptr] == '0' else 0
            ptr += 1
    return tot
