def MEX(L):
    S = 0
    if len(L[0]) == 0:
        return 0
    l = []
    for (c1, c2) in zip(L[0], L[1]):
        if c1 != c2:
            l.append(2)
        elif c1 == '0':
            l.append(1)
        else:
            l.append(0)
    prev = l[0]
    S += prev
    for i in range(1, len(l)):
        if l[i] == 1 and prev == 0:
            S += 2
            prev = 2
        elif l[i] == 0 and prev == 1:
            S += 1
            prev = 2
        else:
            S += l[i]
            prev = l[i]
    return S
