def rotate(array: list, r: int, l: int) -> list:
    array[r:l + 1] = [array[l]] + array[r:l]
    return array

def find(array: list, start: int, val: int) -> int:
    for x in range(start, len(array)):
        if array[x] == val:
            return x
    return -1

