    l = [int(i) for i in input().split()]
    ans = (1 << 20) - 1
    for i in range(n):
        if i != l[i]:
            ans &= l[i]
    print(ans)

for _ in range(int(input())):
    n = int(input())
    nums = list(map(int, input().split()))
    ans = -1
    for i in range(0, n):
        if i != nums[i]:
            if ans == -1:
                ans = nums[i]
            else:
                ans = ans & nums[i]
    print(ans)
