from sys import stdin
input = stdin.readline

def func():
    if s.count(c) == n:
        print(0)
        return
    i = 1
    while i < n:
        if s[i] == c:
            curr_ans = True
            for j in range(i, n, i):
                if s[j] != c:
                    curr_ans = False
                    break
            if curr_ans:
                print(1)
                print(i)
                return
        i += 1
    print(2)
    print(n - 2, n - 1)
for _ in range(int(input())):
    (n, c) = input().split()
    s = c + input()
    n = int(n) + 1
    func()

for _ in range(int(input())):
    l = list(input().split())
    n = int(l[0])
    c = l[1]
    s = input()
    a = 0
    for i in range(n):
        if s[i] != c:
            a += 1
            k = i
    if a == 0:
        print(0)
    elif a == 1:
        if k == n - 1:
            print(1)
            print(k)
        else:
            print(1)
            print(k + 2)
    else:
        flag = 0
        for i in range(n - 1, n // 2 - 1, -1):
            if s[i] == c:
                flag = i
                break
        if flag:
            print(1)
            print(flag + 1)
        else:
            print(2)
            print(n, n - 1)
