def max_amount_of_local(n):
    if n == 2:
        return 0
    if n == 3:
        return 1
    if n == 4:
        return 1
    if n == 5:
        return 2
    return (n - 1) // 2

def score(res):
    min_amount = 0
    max_amount = 0
    for i in range(1, len(res) - 1):
        if res[i] > max(res[i - 1], res[i + 1]):
            max_amount += 1
        if res[i] < min(res[i - 1], res[i + 1]):
            min_amount += 1
    return (max_amount, min_amount)

def solve(n, a, b):
    if max_amount_of_local(n) < max(a, b):
        return -1
    if a + b > n - 2:
        return -1
    if abs(a - b) > 1:
        return -1
    if a < b:
        return [n + 1 - val for val in solve(n, b, a)]
    res = [1]
    cur_max = n
    cur_min = 2
    while b > 0:
        res.append(cur_max)
        res.append(cur_min)
        cur_max -= 1
        cur_min += 1
        a -= 1
        b -= 1
    if a == 0:
        for i in range(cur_min, cur_max + 1):
            res.append(i)
        return res
    res.append(cur_max)
    cur_max -= 1
    for i in range(cur_max, cur_min - 1, -1):
        res.append(i)
    return res
