from collections import deque

def solution(n, a, b):
    limit = (n - 1) // 2
    if a > limit or b > limit or a + b > 2 * limit - n % 2 or (abs(a - b) > 1):
        return [-1]
        _hy_anon_var_1 = None
    else:
        _hy_anon_var_1 = None
    rst = [1]
    nums = deque(range(2, n + 1))
    M = max(a, b)
    m = min(a, b)
    for _ in range(m):
        rst.append(nums.pop())
        rst.append(nums.popleft())
    if M > m:
        while nums:
            rst.append(nums.pop())
        _hy_anon_var_2 = None
    else:
        while nums:
            rst.append(nums.popleft())
        _hy_anon_var_2 = None
    return rst if a >= b else [n + 1 - i for i in rst]
