def f(n, a, b):
    if abs(a - b) > 1 or a + b > n - 2:
        return [-1]
    perm = []
    if a >= b:
        for i in range(1, n - a - b):
            perm.append(i)
        lower = n - a - b
        upper = n
        while lower < upper:
            perm.append(upper)
            perm.append(lower)
            upper -= 1
            lower += 1
        if lower == upper:
            perm.append(lower)
    else:
        for i in range(n - a - b, 0, -1):
            perm.append(i)
        lower = n - a - b + 1
        upper = n
        while lower < upper:
            perm.append(upper)
            perm.append(lower)
            upper -= 1
            lower += 1
        if lower == upper:
            perm.append(lower)
    return perm
