def createlist(n, a, b):
    if abs(a - b) > 1 or a + b > n - 2:
        return [-1]
    else:
        list1 = [i for i in range(1, n + 1)]
        if a > b:
            list1 = list1[::-1]
            for i in range(0, a + b, 2):
                (list1[i], list1[i + 1]) = (list1[i + 1], list1[i])
        elif a < b:
            for i in range(0, a + b, 2):
                (list1[i], list1[i + 1]) = (list1[i + 1], list1[i])
        else:
            for i in range(1, a + b, 2):
                (list1[i], list1[i + 1]) = (list1[i + 1], list1[i])
        return list1
