    s = list(input().strip())
    t = input().strip()
    idx1 = start = 0
    while idx1 < n:
        (c1, c2) = (s[idx1], t[idx1])
        if c1 == c2:
            idx1 += 1
            continue
        if c1 == 'c':
            return 'NO'
        elif c1 == 'a':
            if c2 == 'c':
                return 'NO'
            j = max(start, idx1 + 1)
            while j < n and s[j] != 'b':
                if s[j] == 'c':
                    return 'NO'
                j += 1
            if j == n:
                return 'NO'
            (s[idx1], s[j]) = (s[j], s[idx1])
            start = j + 1
        else:
            if c2 == 'a':
                return 'NO'
            j = max(start, idx1 + 1)
            while j < n and s[j] != 'c':
                if s[j] == 'a':
                    return 'NO'
                j += 1
            if j == n:
                return 'NO'
            (s[idx1], s[j]) = (s[j], s[idx1])
            start = j + 1
        idx1 += 1
    if s[-1] != t[-1]:
        return 'NO'
    return 'YES'
