    sl = list(s)
    j = -1
    for i in range(n):
        if sl[i] == t[i]:
            continue
        if sl[i] == 'a' and t[i] == 'b':
            if j < i:
                j = i
            while j < n and sl[j] == 'a':
                j += 1
            if j < n and sl[j] == 'b':
                sl[j] = 'a'
                continue
            else:
                return 'NO'
        if sl[i] == 'b' and t[i] == 'c':
            if j < i:
                j = i
            while j < n and sl[j] == 'b':
                j += 1
            if j < n and sl[j] == 'c':
                sl[j] = 'b'
                continue
            else:
                return 'NO'
        return 'NO'
    return 'YES'

