def cal(n, s, t):
    if s == t:
        return True
    (p, q) = (list(s), list(t))
    i = n - 1
    (a, b) = (n - 1, n - 1)
    while i >= 0:
        if q[i] == p[i]:
            i -= 1
            continue
        if q[i] == 'c':
            return False
        if q[i] == 'b':
            ck = 0
            for j in range(min(i, b), -1, -1):
                if p[j] == 'a':
                    return False
                elif p[j] == 'b':
                    b = j
                    (p[j], p[i]) = (p[i], p[j])
                    ck = 1
                    break
            if ck == 0:
                return False
        else:
            ck = 0
            for j in range(min(i, a), -1, -1):
                if p[j] == 'c':
                    return False
                elif p[j] == 'a':
                    a = j
                    (p[j], p[i]) = (p[i], p[j])
                    ck = 1
                    break
            if ck == 0:
                return False
        i -= 1
    return True
