    (q, r) = (0, 0)
    for i in range(len(l)):
        if m[i] == 'c':
            q = max(i, q)
            while q < len(l):
                if l[q] == 'c':
                    (l[i], l[q]) = (l[q], l[i])
                    q += 1
                    break
                elif l[q] == 'a':
                    return 'NO'
                q += 1
        if m[i] == 'b':
            r = max(i, r)
            while r < len(l):
                if l[r] == 'b':
                    (l[i], l[r]) = (l[r], l[i])
                    r += 1
                    break
                elif l[r] == 'c':
                    return 'NO'
                r += 1
    if l == m:
        return 'YES'
    return 'NO'
