N = int(input())
from collections import defaultdict
M = defaultdict(lambda : [])
for i in range(1, int(100000.0) + 1):
    for j in range(i, int(100000.0) + 1, i):
        M[j].append(i)

def solve():
    (n, m) = [int(x) for x in input().split()]
    A = [int(x) for x in input().split()]
    A.sort()
    freq = {}
    (l, i) = (0, 0)
    res = 100000
    while i < n:
        for fac in M[A[i]]:
            if fac > m:
                break
            if fac not in freq:
                freq[fac] = 0
            freq[fac] += 1
        while len(freq) == m:
            res = min(res, A[i] - A[l], res)
            for fac in M[A[l]]:
                if fac > m:
                    break
                freq[fac] -= 1
                if freq[fac] == 0:
                    del freq[fac]
            l += 1
        i += 1
    print(res if res != 100000 else -1)
for i in range(N):
    solve()

