def solve(L):
    a = 0
    b = 0
    k = 0
    for i in range(len(L)):
        if L[i] != i + 1:
            a += 1
        if L[i] != len(L) - i:
            b += 1
        if L[i] not in [i + 1, len(L) - i]:
            k += 1
    if b - a >= k:
        return 'First'
    if b + 1 + k <= a:
        return 'Second'
    return 'Tie'
