def binarysearch(array, element):
    if len(array) == 1:
        if array[0] >= element:
            return 0
        else:
            return 1
    elif array[len(array) // 2] >= element:
        return binarysearch(array[0:len(array) // 2], element)
    else:
        return len(array) // 2 + binarysearch(array[len(array) // 2:], element)

def reverse(obj):
    l = obj[::-1]
    return l
nm = input().split(' ')
n = int(nm[0])
m = int(nm[1])
data = input().split(' ')
for i in range(len(data)):
    data[i] = int(data[i])
data.sort()
x = m
final = 0
i = binarysearch(data, m)
if i < n:
    for j in range(i, n):
        final = final + 1 + data[j]
l1 = data[0:i]
l2 = reverse(l1)
x = 0
j = 0
while x + j <= i - 1:
    final += l2[x]
    if l1[j] + l2[x] <= m:
        if not x + j == i - 1:
            final += l1[j]
            j += 1
            if x + j == i - 1:
                final += 1
        else:
            final += 1
    else:
        final += 1
    x += 1
print(final)
