def solve(nums):
    c = nums[-1]
    s = set(nums)
    n = len(nums)
    if 1 not in s:
        return False
    else:
        k = 0
        for i in range(1, c + 1):
            if i not in s:
                for j in range(n):
                    if nums[j] > c // i:
                        break
                    while nums[k] < i * nums[j]:
                        k += 1
                    if nums[k] < (i + 1) * nums[j]:
                        return False
    return True
for _ in range(int(input())):
    (n, c) = list(map(int, input().split(' ')))
    nums = list(map(int, input().split(' ')))
    nums = sorted(list(set(nums)))
    if solve(nums):
        print('Yes')
    else:
        print('No')

