    while True:
        while i < n - 1 and s[i] == s[i + 1]:
            i += 1
        while j < n - 1 and t[j] == t[j + 1]:
            j += 1
        if i < n - 1 and j < n - 1:
            edits += abs(i - j)
        elif i == n - 1 and j == n - 1:
            return edits + abs(i - j)
        else:
            return -1
        i += 1
        j += 1
