for _ in range(int(input())):
    (n, k) = map(int, input().split())
    a = list(map(int, input().split()))
    r = k
    k -= 1
    l = k - 1
    x = y = 0
    mr = ml = 0
    while l >= 0 and r < n:
        if r < n and a[k] + a[r] + y + ml >= 0:
            y += a[r]
            mr = max(y, mr)
            r += 1
        elif l >= 0 and a[k] + a[l] + x + mr >= 0:
            x += a[l]
            ml = max(x, ml)
            l -= 1
        else:
            break
    if l == -1 or r == n:
        print('YES')
    else:
        print('NO')

