import sys
input = sys.stdin.readline

def read_int_list():
    return list(map(int, input().split()))

def solve(a):
    max_value = max(a)
    present = [False] * (max_value + 1)
    for value in a:
        present[value] = True
    count = 0
    for i in range(max_value, 0, -1):
        first = -1
        second = -1
        if present[i]:
            continue
        for j in range(2 * i, max_value + 1, i):
            if present[j]:
                if first == -1:
                    first = j
                elif j % first != 0:
                    second = j
                    break
        if first != -1 and second != -1:
            count += 1
            present[i] = True
    return count
n = int(input())
a = read_int_list()
print(solve(a))

from math import gcd

def problemD(n, nums):
    maxNum = max(nums)
    isPresent = [False] * (maxNum + 1)
    for num in nums:
        isPresent[num] = True
    ans = 0
    for i in range(1, maxNum + 1):
        if isPresent[i]:
            continue
        num = 0
        for j in range(2 * i, maxNum + 1, i):
            if isPresent[j]:
                if num == 0:
                    num = j
                else:
                    num = gcd(num, j)
                    if num == i:
                        ans += 1
                        isPresent[i] = True
                        break
    return ans
n = int(input())
nums = list(map(int, input().split()))
print(problemD(n, nums))
