(n, q) = map(int, input().strip().split())
qs = [[] for _ in range(n)]
refers_self = [False for _ in range(n)]
for _ in range(q):
    (i, j, x) = map(int, input().strip().split())
    if i == j:
        refers_self[i - 1] = True
    qs[i - 1].append((j - 1, x))
    qs[j - 1].append((i - 1, x))
a = []
for i in range(n):
    if qs[i]:
        ans = (2 << 32) - 1
        for (j, x) in qs[i]:
            ans = ans & x
        a.append(ans)
    else:
        a.append(0)
for i in range(n):
    if refers_self[i]:
        continue
    ans = (2 << 32) - 1
    for (j, x) in qs[i]:
        ans = ans & a[j]
    a[i] = a[i] - (a[i] & ans)
print(*a)
