from collections import defaultdict
import sys
input = sys.stdin.readline

def get_remainder(i, j):
    if i == 0:
        return pref[j]
    else:
        return (pref[j] - pref[i - 1]) % 9
for _ in range(int(input())):
    s = [int(i) for i in input().strip()]
    n = len(s)
    pref = []
    cur = 0
    for i in range(n):
        cur = cur * 10 + s[i]
        cur %= 9
        pref.append(cur)
    (w, m) = map(int, input().split())
    dic = defaultdict(lambda : [])
    for i in range(n - w + 1):
        cur = get_remainder(i, i + w - 1)
        dic[cur].append(i)
    ans = []
    for qi in range(m):
        (l, r, k) = map(int, input().split())
        cur = get_remainder(l - 1, r - 1)
        flag = 0
        for i in dic:
            req = (-(cur * i) + k) % 9
            lst = [req]
            if req != i and req in dic:
                ans.append([dic[i][0] + 1, dic[req][0] + 1])
                flag = 1
            elif req == i and len(dic[i]) >= 2:
                ans.append([dic[i][0] + 1, dic[i][1] + 1])
                flag = 1
            if flag:
                break
        if not flag:
            ans.append([-1, -1])
    for i in ans:
        print(*i)
