import sys

def solve():
    inp = sys.stdin.readline
    (a, b, c, d) = map(int, inp().split())
    s = inp()
    if s.count('A') != a + c + d or s.count('B') != b + c + d:
        print('NO')
        return
    cc = {'A': [], 'B': []}
    f = 0
    e = 0
    for i in range(1, len(s) - 1):
        if s[i] == s[i - 1]:
            l = i - f
            if l % 2 == 0:
                cc[s[f]].append(l // 2)
            else:
                e += l // 2
            f = i
    l = len(s) - 1 - f
    if l % 2 == 0:
        cc[s[f]].append(l // 2)
    else:
        e += l // 2
    ca = cc['A']
    ca.sort(reverse=True)
    while ca and c > 0:
        w = min(ca[-1], c)
        ca[-1] -= w
        c -= w
        if ca[-1] == 0:
            ca.pop()
    cb = cc['B']
    cb.sort(reverse=True)
    while cb and d > 0:
        w = min(cb[-1], d)
        cb[-1] -= w
        d -= w
        if cb[-1] == 0:
            cb.pop()
    for i in ca:
        e += i - 1
    for i in cb:
        e += i - 1
    if c + d <= e:
        print('YES')
    else:
        print('NO')

