import sys
input = sys.stdin.readline

def solve():
    n = int(input())
    b = list(map(int, input().split()))
    ans = [[0] * n for _ in range(n)]
    for i in range(n):
        ans[i][i] = b[i]
        j = i
        while j > 0:
            ans[i][j - 1] = (ans[i][j] - i) % n
            j -= 1
        j = i
        while j < n - 1:
            ans[i][j + 1] = (ans[i][j] + i) % n
            j += 1
    for row in ans:
        print(*row)
for _ in range(1):
    solve()

