for i in range(int(input())):
    s = input()
    t = input()
    p = [0]
    for i in range(len(s) - len(t) + 2):
        if s[i:i + len(t)] == t:
            p.append(i)
    f = [1000] * (len(p) + 1)
    g = [0] * (len(p) + 1)
    p[0] = -1000
    g[0] = 1
    f[0] = 0
    p.append(len(s) + len(t))
    for i in range(len(p)):
        j = i + 1
        while j < len(p) and p[j] <= p[i] + len(t) - 1:
            j += 1
        k = j
        while k < len(p) and p[k] <= p[j] + len(t) - 1:
            if f[i] + 1 < f[k]:
                f[k] = f[i] + 1
                g[k] = g[i]
            elif f[i] + 1 == f[k]:
                g[k] = (g[k] + g[i]) % int(1000000000.0 + 7)
            k += 1
    print(f[-1] - 1, g[-1])

