I = lambda : [int(i) for i in input().split()]
n = int(input())
A = I()
a = {x: i for (i, x) in enumerate(A)}
(w, v, u) = (0, 0, 0)
for (i, x) in enumerate(A):
    w = max(w, a[x])
    if v > i:
        u += 1
    else:
        v = w
print(u)

n = int(input())
a = [int(i) for i in input().split()]
last = {x: i for (i, x) in enumerate(a)}
(lastest, covered, ans) = (0, 0, 0)
for (i, x) in enumerate(a):
    lastest = max(lastest, last[x])
    if covered > i:
        ans += 1
    else:
        covered = lastest
