    kranges = [0] * (n + 1)
    mono = [int(a) for a in input().split()]
    poly = [int(b) for b in input().split()]
    for i in range(n):
        allowedk = []
        diff = mono[i] - poly[i]
        if diff >= 1:
            kranges[poly[i]] += 1
            kranges[mono[i]] -= 1
    newk = []
    for i in range(1, n + 1):
        kranges[i] += kranges[i - 1]
    for i in range(1, n + 1):
        accept = True
        for j in range(i, n + 1, i):
            if kranges[j] != 0:
                accept = False
        if accept:
