for t in range(int(input())):
    n = int(input())
    a = list(map(int, input().split()))
    AA = now = 0
    for i in range(n):
        now ^= a[i]
        if not now and (not i % 2):
            AA = i + 1
    if not AA or now:
        print('NO')
    else:
        print('YES')
        an = []
        for i in range(1, AA, 2):
            an.append(str(i))
        for i in range(AA + 1, n - 1, 2):
            an.append(str(i))
        an.extend(an[::-1])
        print(len(an))
        print(' '.join(an))

