import math
(n, k) = map(int, input().split(' '))
m = int(math.ceil(math.log(n, k)))
print(m)
f = [0 for i in range(n * (n - 1) // 2)]
s = 1
while True:
    s *= k
    for l in range(1, n + 1, s):
        for i in range(l, min(l + s, n + 1)):
            for j in range(i + 1, min(l + s, n + 1)):
                g = lambda i, j: (n - 1 + (n - i + 1)) * (i - 1) // 2 + (j - i - 1)
                x = g(i, j)
                f[x] = max(f[x], m)
    m -= 1
    if m == 0:
        break
for x in f:
    print(x, end=' ')
