t = int(input(''))
for _ in range(t):
    n = int(input(''))
    a = (input(''), input(''), input(''))
    if a[0][n - 1] == a[1][n - 1] and a[0][n - 1] == a[2][n - 1]:
        print(0)
        continue
    big = [0, 0, 0]
    for i in (0, 1, 2):
        for j in range(n - 2, -1, -1):
            if a[i][j] != a[i][n - 1]:
                big[i] = j + 1
                break
    if a[0][n - 1] == a[1][n - 1]:
        w = 2
    if a[0][n - 1] == a[2][n - 1]:
        w = 1
    if a[2][n - 1] == a[1][n - 1]:
        w = 0
    print(min(n, min(n - big[w], n - big[(w + 1) % 3]) + min(n - big[w], n - big[(w + 2) % 3])))

