class Solution:

    def appartments(self, n):
        tns = n % 10 - 1
        totalDig = 0
        while n > 0:
            n //= 10
            totalDig += 1
        res = tns * 10
        for i in range(1, 5):
            res += i
            if totalDig == i:
                break
        return res
