    count = 0
    left = 0
    right = n - 1
    while right >= 0:
        if arr[right] == 1:
            break
        right -= 1
    while left < n:
        if arr[left] == 1:
            break
        left += 1
    for i in range(left, right):
        if arr[i] == 0:
            count += 1
    return count
