for _ in range(int(input())):
    n = int(input())
    s = input()
    if s.count('0') > s.count('1'):
        print('0' * n)
    else:
        print('1' * n)

t = int(input())
answer = []
for i in range(t):
    n = int(input())
    s = input()
    count = 0
    for num in s:
        if num == '0':
            count += 1
    if count >= n:
        answer.append('0' * n)
    else:
        answer.append('1' * n)
for string in answer:
    print(string)

for _ in range(int(input())):
    n = int(input())
    bin_string = str(input())
    value = []
    for i in range(n):
        value.append(bin_string[i:i + n])
    store = 0
    for substring_value in value:
        if '1' in substring_value:
            store += 1
    if store == len(value):
        print('1' * n)
    else:
        print('0' * n)
