def segregateEvenOdd(arr, n):
    (left, right) = (0, n - 1)
    while left < right:
        while arr[left] % 2 == 0 and left < right:
            left += 1
        while arr[right] % 2 != 0 and left < right:
            right -= 1
        if left < right:
            (arr[left], arr[right]) = (arr[right], arr[left])
            left += 1
            right = right - 1

def solve():
    n = readInt()
    a = readInts()
    segregateEvenOdd(a, n)
    for i in a:
        print(i, end=' ')
    print()

