def get_primeFactor(n):
    res = [1]
    x = 2
    while x * x <= n:
        while n % x == 0:
            res.append(x)
            n //= x
        x += 1
    if n > 1:
        res.append(n)
    return res

def getFactor(n):
    res = {1}
    x = 2
    while x * x <= n:
        if n % x == 0:
            res.add(x)
            res.add(n // x)
        x += 1
    return res
